exports.isValidDate = function(dateString){
	var isValid = true;

	var newNoteDate =  new Date(dateString);

	if(newNoteDate.getMonth() > 0 )
	{
		isValid = true;
	}else{
		isValid = false;
	}	
	return isValid;
};



